// RegisterForm.js
import "../styles/RegisterForm.css"
import React, { useState } from 'react';
import ApiInteraction from './ApiInteraction';
import axios from "axios";




const RegisterForm = () => {

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  
  const handleLogin = async () => {
    try {
      // Make the POST request
      const response = await axios.post(
        'https://dev-mrp.insby.tech/api/session/customer-sign-in',
        {
          autoRegister: true,
          email,
          password,
          confirmPassword,
        }
      );

      // Handle the response, e.g., update the UI or redirect
      console.log('Login Successful:', response.data);
    } catch (error) {
      // Handle errors, e.g., display an error message
      console.error('Login Error:', error);
    }
  };

  return (
    <div>
        <div className='title'>
      <h2>Sign Up</h2>
      <p>Enter your details to get started.</p>
      </div>
      <form>
        <div>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)}  placeholder='Enter your email address'/>
        </div>
        <div>
          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder='Enter a strong password'/>
        </div>
        <div>
          <label>Confirm Password</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)}  placeholder='Confirm your password'/>
        </div>
        <div>
          <button className='registration-button'  type="submit"  onClick={handleLogin} >
            Register
          </button>
        </div>
        <div>
            <span> Already have an account?</span> <a className="log-in-page" href='http://localhost:3000/login'> Log in</a>
        </div>
        <ApiInteraction />
      </form>
    </div>
  );
};

export default RegisterForm;
