import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Product = () => {

 



  // State to store product information
  const [product, setProduct] = useState(null);

  

  // Effect to fetch product information on component mount
  useEffect(() => {
    const fetchProduct = async () => {
      const apiUrl = 'https://dev-mrp.insby.tech/api';

  const authToken = localStorage.getItem('authToken');
  axios.get(`${apiUrl}/v2/session/product`,{
    headers: {
      Authorization: `Bearer ${authToken}`,
    },
  }).then(response =>{
    response.data
    console.log('product information', response.data)
  }).catch(error =>{
    console.log('Error fetching product infromation', error)
  })
    };

    fetchProduct();
  }, []);


  return (
    <div>
      <h1>Product Page</h1>
      {product ? (
        <div>
          <h2>{product.name}</h2>
          <img src={product.image} alt={product.name} style={{ maxWidth: '100%' }} />
          <p>{product.description}</p>
          <button onClick={() => console.log('Regular Price Clicked')}>Regular Price</button>
          <button onClick={() => console.log('Member Price Clicked')}>Member Price</button>
        </div>
      ) : (
        <p>Loading product information...</p>
      )}
    </div>
  );
};

export default Product;
