// ApiInteraction.js
import React, { useState } from 'react';
import axios from 'axios';

const ApiInteraction = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      // Initialize the application
      await axios.post('https://dev-mrp.insby.tech/api/v2/init/app', {
        uuid: 'your-uuid', // replace with your actual uuid
        uuidOS: 'Windows',
      });

      // Sign in or register a customer
      const response = await axios.post('https://dev-mrp.insby.tech/api/session/customer-sign-in', {
        autoRegister: true,
        login: email,
        password: password,
        confirmPassword: password, // Assuming you want to use the same password for confirmation
      });

      // Handle the response
      console.log('Login successful:', response.data);

      // Add additional logic here based on the response
    } catch (error) {
      // Handle errors
      console.error('Login failed:', error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </div>
      </form>
    </div>
  );
};

export default ApiInteraction;
