// ApiInteraction.js
import React, { useState } from 'react';
import axios from 'axios';

const ApiInteraction = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  
    const handleLogin = async () => {
      try {
        // Initialize the application
        await axios.post('https://dev-mrp.insby.tech/api/v2/init/app', {
          uuid: 'uuidv4', // replace with your actual uuid
          uuidOS: 'Windows',
        });
  
        // Sign in or register a customer
        const response = await axios.post('https://dev-mrp.insby.tech/api/session/customer-sign-in', {
          
          login: email,
          password: password,
          confirmPassword: password,
        });
  
        // Handle the response
        console.log('Login successful:', response.data);
  
        // Save the authentication token to localStorage
        const authToken = response.data.token;
        localStorage.setItem('authToken', authToken);
  
        // Add additional logic here based on the response
      } catch (error) {
        // Handle errors
        console.error('Login failed:', error);
      }
    };

  return (
    <div>
      
    </div>
  );
};

export default ApiInteraction;
