// Import the corresponding CSS file for styling
import "../styles/LoginForm.css"
// Import the ApiInteraction component to handle API interactions
import ApiInteraction from "../components/ApiInteraction"

// Define the LoginForm component
const LoginForm = () => {
  return (
    <div>
      {/* Heading for the login form */}
      <h2>Log in</h2>
      {/* Form element for user input */}
      <form>
        {/* Input field for email address */}
        <div>
          <label>Email address</label>
          <br></br>
          <input className="email-address-login" type="email" placeholder='Enter your email' />
        </div>
        {/* Input field for password */}
        <div>
          <label>Password</label>
          <br></br>
          <input className="password-login" type="password" placeholder='Enter your password'/>
        </div>
        {/* Button to submit the login form */}
        <div>
          <button className='login-button'>Log in</button>
        </div>
        {/* Section for creating a new account */}
        <div className='create-account'>
          <span>Don't have an account?</span>
          {/* Link to the registration page */}
          <a className='register-button' href='http://localhost:3000/register'>Register</a>
        </div>
        {/* Include the ApiInteraction component for handling API calls */}
        <ApiInteraction />
      </form>
    </div>
  );
};

// Export the LoginForm component
export default LoginForm;
