import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Product = () => {

  // State to store product information
  const [products, setProducts] = useState([]);

  const [showMemberPrice, setShowMemberPrice] = useState(false);

  

  // Effect to fetch product information on component mount
  useEffect(() => {
    const fetchProducts  = async () => {
      const apiUrl = 'https://dev-mrp.insby.tech/api';

  const authToken = localStorage.getItem('authToken');
  axios.get(`${apiUrl}/v2/session/product`,{
    headers: {
      Authorization: `Bearer ${authToken}`,
    },
  }).then(response =>{
    setProducts(response.data.data)
    console.log('product information', response.data.data)
  }).catch(error =>{
    console.log('Error fetching product infromation', error)
  })
    };

    fetchProducts();
  }, []);


  return (
    <div>
      <h1>Product Page</h1>
      {products.map((product, index) => (
        <div key={index}>
          <h2>{product.name}</h2>
          <p>{product.title}</p>
          <img src={product.image_url} alt={product.name} style={{ maxWidth: '100%' }} />
          <p>{product.body}</p>
          <button onClick={() => console.log(`Regular Price Clicked for : ${product.prices[0].price} $`)}>
            Regular price: {product.prices[0].price} $
          </button>
          <button onClick={() => setShowMemberPrice(!showMemberPrice)}>
            {showMemberPrice ? `Member Price: ${product.prices[0].member_price} $` : 'Member Price'}
          </button>
        </div>
      ))}
    </div>
  );
};

export default Product;
