// RegisterForm.js
import "../styles/RegisterForm.css"
import React, { useState } from 'react';
import ApiInteraction from './ApiInteraction';
import axios from "axios";




const RegisterForm = () => {

  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Check if passwords match before sending the request
      if (formData.password !== formData.confirmPassword) {
        console.error('Passwords do not match');
        return;
      }

      // Send POST request using Axios
      const response = await axios.post(' https://dev-mrp.insby.tech/api/session/customer-sign-in', {
        email: formData.email,
        password: formData.password,
        // Add other fields as needed
      });

      // Handle the response as needed
      console.log('Response:', response.data);
    } catch (error) {
      // Handle errors
      console.error('Error:', error);
    }
  };


  return (
    <div>
        <div className='title'>
      <h2>Sign Up</h2>
      <p>Enter your details to get started.</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange}  placeholder='Enter your email address'/>
        </div>
        <div>
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder='Enter a strong password'/>
        </div>
        <div>
          <label>Confirm Password</label>
          <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} placeholder='Confirm your password'/>
        </div>
        <div>
          <button className='registration-button'  type="submit" >
            Register
          </button>
        </div>
        <div>
            <span> Already have an account?</span> <a className="log-in-page" href='http://localhost:3000/login'> Log in</a>
        </div>
        <ApiInteraction />
      </form>
    </div>
  );
};

export default RegisterForm;
