import React from 'react';
import axios from 'axios';

const Product = ({ title, description, image, memberPrice, regularPrice }) => {

  const apiUrl = 'https://dev-mrp.insby.tech/api';
  const username = 'hQtwpolwKTjUkNAkZGeSiOkhp2OP8UA6TAPyA7bOWLFXTPPJOMzQUOOhLg43uXoFIuA5T4yKySJnHZhhVNWBqfNLcaKBfrAx';
  const password = 'lolci3wdjsHDhFsQOnubYma5Zl33BPwE4NA5wftU9qxJnmIkP3ju8qw0F6ECjF4kvmp3SwNuLZrEMQezkFHqOMYjCBVJJzxv';

// Encode credentials for Basic Authentication
const credentials = btoa(`${username}:${password}`);
const headers = { Authorization: `Basic ${credentials}` };

// Example: POST /v2/init/app
const initApp = async () => {
try {
  const response = await axios.post(
    `${apiUrl}/v2/init/app`,
    {
      uuid: 'uuidv4',  // Replace with an actual UUID
      uuidOS: 'Windows',
    },
    { headers }
  );
  const authToken = response.data.data.token;
  localStorage.setItem('authToken', authToken);

  console.log('Init App Response:', response.data);
} catch (error) {
  console.error('Init App Error:', error);
}
};






initApp();

  const authToken = localStorage.getItem('authToken');
  axios.get(`${apiUrl}/v2/session/product`,{
    headers: {
      Authorization: `Bearer ${authToken}`,
    },
  }).then(response =>{
    console.log('product information', response.data)
  }).catch(error =>{
    console.log('Error fetching product infromation', error)
  })

  return (
    <div>
      <img src={image} alt={title} />
      <h3>{title}</h3>
      <p>{description}</p>
      <button>{`Member Price: $${memberPrice}`}</button>
      <button>{`Regular Price: $${regularPrice}`}</button>
    </div>
  );
};

export default Product;
