// RegisterForm.js
import React, { useState } from 'react';
import ApiInteraction from './ApiInteraction';
import axios from 'axios';



const RegisterForm = ({ onRegister, onLoginRedirect }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleRegister = async () => {
    try {
      // Initialize the application
      await axios.post('https://dev-mrp.insby.tech/api/v2/init/app', {
        uuid: 'uuidv4', // replace with your actual uuid
        uuidOS: 'Windows',
      });

      // Register a new customer
      const response = await axios.post('https://dev-mrp.insby.tech/api/session/customer-sign-in', {
        autoRegister: true,
        login: email,
        password: password,
        confirmPassword: confirmPassword,
      });

      // Handle the response
      console.log('Registration successful:', response.data);

      // Save the authentication token to localStorage
      const authToken = response.data.token;
      localStorage.setItem('authToken', authToken);
      console.log('Received token:', authToken);

      // Add additional logic here based on the response
    } catch (error) {
      // Handle errors
      console.error('Registration failed:', error);
    }
  };

  return (
    <div>
        <div className='title'>
      <h2>Sign in</h2>
      <p>Enter your details to get started.</p>
      </div>
      <form>
        <div>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder='Enter your email address'/>
        </div>
        <div>
          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder='Enter a strong password'/>
        </div>
        <div>
          <label>Confirm Password</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} placeholder='Confirm your password'/>
        </div>
        <div>
          <button className='registration-button' type="submit" onClick={handleRegister} style={{ backgroundColor: 'red', color: 'white' }}>
            Register
          </button>
        </div>
        <div>
            <span> Already have an account?</span> <a className="log-in-page" href='http://localhost:3000/login'> Log in</a>
        </div>
        <ApiInteraction />
      </form>
    </div>
  );
};

export default RegisterForm;
