import React, { useState } from 'react';
import "../styles/LoginForm.css"
import axios from 'axios';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      // Encode credentials for Basic Authentication
      const credentials = btoa(`${username}:${password}`);

      // Make a POST request to the endpoint with Basic Authentication
      const response = await axios.post('https://dev-mrp.insby.tech/api/v2/init/app', null, {
        headers: {
          Authorization: `Basic ${credentials}`,
          'Content-Type': 'application/json',
        },
      });

      // Handle the response
      console.log('Login successful:', response.data);

      // Add additional logic here based on the response
    } catch (error) {
      // Handle errors
      console.error('Login failed:', error);
    }
  };

  return (
        <div>
        <h2>Log in</h2>
        <form>
            <div>
            <label>Email address</label>
            <br></br>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div>
            <label>Password</label>
            <br></br>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)}  placeholder='Enter your password'/>
            </div>
            <div>
            <button className='login-button'  onClick={handleLogin} >
                Log in
            </button>
            </div>
            <div className='create-account'>
            <span>Don't have account?</span><a className='register-button' href='http://localhost:3000/register'>Register</a>
            </div>
        </form>
        </div>
  );
};

export default LoginForm;
