// RegisterForm.js
import React, { useState } from 'react';
import '..styles/Register.css';

const RegisterForm = ({ onRegister, onLoginRedirect }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleRegister = () => {
    // Handle registration logic here
    onRegister(email, password, confirmPassword);
  };

  return (
    <div>
      <h2>Registration</h2>
      <p>Enter your details below to create an account:</p>
      <form>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <div>
          <button type="button" onClick={handleRegister} style={{ backgroundColor: 'red', color: 'white' }}>
            Register
          </button>
        </div>
        <div>
          <button type="button" onClick={onLoginRedirect}>
            Already have an account? Login
          </button>
        </div>
      </form>
    </div>
  );
};

export default RegisterForm;
