import React, { useState } from 'react';
import axios from 'axios';

const ApiInteraction = () => {
    const apiUrl = 'https://dev-mrp.insby.tech/api';
const username = 'hQtwpolwKTjUkNAkZGeSiOkhp2OP8UA6TAPyA7bOWLFXTPPJOMzQUOOhLg43uXoFIuA5T4yKySJnHZhhVNWBqfNLcaKBfrAx';
const password = 'lolci3wdjsHDhFsQOnubYma5Zl33BPwE4NA5wftU9qxJnmIkP3ju8qw0F6ECjF4kvmp3SwNuLZrEMQezkFHqOMYjCBVJJzxv';

// Encode credentials for Basic Authentication
const credentials = btoa(`${username}:${password}`);
const headers = { Authorization: `Basic ${credentials}` };

// Example: POST /v2/init/app
const initApp = async () => {
  try {
    const response = await axios.post(
      `${apiUrl}/v2/init/app`,
      {
        uuid: 'uuidv4',  // Replace with an actual UUID
        uuidOS: 'Windows',
      },
      { headers }
    );

    console.log('Init App Response:', response.data);
  } catch (error) {
    console.error('Init App Error:', error);
  }
};

// Example: GET /v2/session/product
const getProductSession = async () => {
  try {
    const response = await axios.get(`${apiUrl}/v2/session/product`, { headers });

    console.log('Product Session Response:', response.data);
  } catch (error) {
    console.error('Product Session Error:', error);
  }
};

// Example: POST /session/customer-sign-in
const customerSignIn = async () => {
  try {
    const response = await axios.post(
      `${apiUrl}/session/customer-sign-in`,
      {
        autoRegister: true,
        login: 'test@gmail.com',
        password: '123',
        confirmPassword: '123',
      },
      { headers }
    );

    console.log('Customer Sign-In Response:', response.data);

    // Save authentication token to localStorage or handle as needed
    const authToken = response.data.token;
    localStorage.setItem('authToken', authToken);
  } catch (error) {
    console.error('Customer Sign-In Error:', error);
  }
};

// Call the functions to make the requests
initApp();
getProductSession();
customerSignIn();

