import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Product = () => {

  // State to store product information
  const [product, setProduct] = useState(null);

  // Effect to fetch product information on component mount
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        // Replace 'YOUR_API_ENDPOINT' with the actual API endpoint for product information
        const response = await axios.get('YOUR_API_ENDPOINT');
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product information:', error);
      }
    };

    fetchProduct();
  }, []);

  return (
    <div>
      <h1>Product Page</h1>
      {product ? (
        <div>
          <h2>{product.name}</h2>
          <img src={product.image} alt={product.name} style={{ maxWidth: '100%' }} />
          <p>{product.description}</p>
          <button onClick={() => console.log('Regular Price Clicked')}>Regular Price</button>
          <button onClick={() => console.log('Member Price Clicked')}>Member Price</button>
        </div>
      ) : (
        <p>Loading product information...</p>
      )}
    </div>
  );
};

export default Product;
