// RegisterForm.js
import "../styles/RegisterForm.css"
import React, { useState } from 'react';
import ApiInteraction from './ApiInteraction';
import axios from "axios";




const RegisterForm = () => {

  const apiUrl = 'https://dev-mrp.insby.tech/api';
  const username = 'hQtwpolwKTjUkNAkZGeSiOkhp2OP8UA6TAPyA7bOWLFXTPPJOMzQUOOhLg43uXoFIuA5T4yKySJnHZhhVNWBqfNLcaKBfrAx';
  const password = 'lolci3wdjsHDhFsQOnubYma5Zl33BPwE4NA5wftU9qxJnmIkP3ju8qw0F6ECjF4kvmp3SwNuLZrEMQezkFHqOMYjCBVJJzxv';

// Encode credentials for Basic Authentication
const credentials = btoa(`${username}:${password}`);
const headers = { Authorization: `Basic ${credentials}` };

  const [formData, setFormData] = useState({
    autoRegister: true,
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Check if passwords match before sending the request
      if (formData.password !== formData.confirmPassword) {
        console.error('Passwords do not match');
        return;
      }

      // Send POST request using Axios
      const response = await axios.post(
        `${apiUrl}/session/customer-sign-in`,
        {
          uuid: 'uuidv4',  
          uuidOS: 'Windows',
        },
        { headers }, {
        email: formData.email,
        password: formData.password,

      });

      // Handle the response as needed
      console.log('Response:', response.data.data);
    } catch (error) {
      // Handle errors
      console.error('Error:', error);
    }
  };


  return (
    <div>
        <div className='title'>
      <h2>Sign Up</h2>
      <p>Enter your details to get started.</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>email</label>
          <input type="email" name="email" value={formData.email} onChange={handleinput}  placeholder='Enter your email address'/>
        </div>
        <div>
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleInput} placeholder='Enter a strong password'/>
        </div>
        <div>
          <label>Confirm Password</label>
          <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleInput} placeholder='Confirm your password'/>
        </div>
        <div>
          <button className='registration-button'  type="submit" >
            Register
          </button>
        </div>
        <div>
            <span> Already have an account?</span> <a className="log-in-page" href='http://localhost:3000/login'> Log in</a>
        </div>
        <ApiInteraction />
      </form>
    </div>
  );
};

export default RegisterForm;
