import React, { useState } from 'react';
import axios from 'axios';

const ApiInteraction = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  const apiUrl = 'https://dev-mrp.insby.tech/api';
  const username = 'hQtwpolwKTjUkNAkZGeSiOkhp2OP8UA6TAPyA7bOWLFXTPPJOMzQUOOhLg43uXoFIuA5T4yKySJnHZhhVNWBqfNLcaKBfrAx';
  const password = 'lolci3wdjsHDhFsQOnubYma5Zl33BPwE4NA5wftU9qxJnmIkP3ju8qw0F6ECjF4kvmp3SwNuLZrEMQezkFHqOMYjCBVJJzxv';

  // Encode credentials for Basic Authentication
  const credentials = btoa(`${username}:${password}`);
  const headers = { Authorization: `Basic ${credentials}` };

  // Example: POST /v2/init/app
  const initApp = async () => {
    try {
      const response = await axios.post(
        `${apiUrl}/v2/init/app`,
        {
          uuid: 'uuidv4',  // Replace with an actual UUID
          uuidOS: 'Windows',
        },
        { headers }
      );
      const authToken = response.data.data.token;

      // Store the token only when the user logs in
      if (loggedIn) {
        localStorage.setItem('authToken', authToken);
      }

      console.log('Init App Response:', response.data);
    } catch (error) {
      console.error('Init App Error:', error);
    }
  };

  // Call initApp when the component mounts or when the user logs in
  React.useEffect(() => {
    initApp();
  }, [loggedIn]);

  return (
    <div></div>
  );
};

export default ApiInteraction;
