// RegisterForm.js
import "../styles/RegisterForm.css"
import React, { useState } from 'react';
import ApiInteraction from './ApiInteraction';
import axios from "axios";




const RegisterForm = () => {

  const apiUrl = 'https://dev-mrp.insby.tech/api';
    const username = 'hQtwpolwKTjUkNAkZGeSiOkhp2OP8UA6TAPyA7bOWLFXTPPJOMzQUOOhLg43uXoFIuA5T4yKySJnHZhhVNWBqfNLcaKBfrAx';
    const password = 'lolci3wdjsHDhFsQOnubYma5Zl33BPwE4NA5wftU9qxJnmIkP3ju8qw0F6ECjF4kvmp3SwNuLZrEMQezkFHqOMYjCBVJJzxv';

// Encode credentials for Basic Authentication
const credentials = btoa(`${username}:${password}`);
const headers = { Authorization: `Basic ${credentials}` };

  const [post, setPost] = useState({
    email:'',
    password: '',
    confrimPassword:','
  })
  const handleInput = (event) =>{
    setPost({...post, [event.target.name]: event.target.value})
  }

  function handleSubmit(event){
    event.preventDefault()
    axios.post(`${apiUrl}/session/customer-sign-in`,
    {
      uuid: 'uuidv4',  // Replace with an actual UUID
      uuidOS: 'Windows',
    },
    { headers })
  }

  return (
    <div>
        <div className='title'>
      <h2>Sign Up</h2>
      <p>Enter your details to get started.</p>
      </div>
      <form onSubmit={handleSubmit} >
        <div>
          <label>Email</label>
          <input type="email" name="email"   placeholder='Enter your email address'/>
        </div>
        <div>
          <label>Password</label>
          <input type="password" name="password"  placeholder='Enter a strong password'/>
        </div>
        <div>
          <label>Confirm Password</label>
          <input type="password" name="confirmPassword"  placeholder='Confirm your password'/>
        </div>
        <div>
          <button className='registration-button'  type="submit" >
            Register
          </button>
        </div>
        <div>
            <span> Already have an account?</span> <a className="log-in-page" href='http://localhost:3000/login'> Log in</a>
        </div>
        <ApiInteraction />
      </form>
    </div>
  );
};

export default RegisterForm;
