import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Products.css'

const Product = () => {

  // State to store product information
  const [products, setProducts] = useState([]);

  const [showPrices, setShowPrices] = useState(false);

  

  // Effect to fetch product information on component mount
  useEffect(() => {
    const fetchProducts  = async () => {
      const apiUrl = 'https://dev-mrp.insby.tech/api';

  const authToken = localStorage.getItem('authToken');
  axios.get(`${apiUrl}/v2/session/product`,{
    headers: {
      Authorization: `Bearer ${authToken}`,
    },
  }).then(response =>{
    setProducts(response.data.data)

  }).catch(error =>{
    console.log('Error fetching product infromation', error)
  })
    };

    fetchProducts();
  }, []);


  return (
    <div className="product-container">
      {products.map((product, index) => (
        <div key={index} className="product-card">
          <div className="image-container">
            <img src={product.image_url} alt={product.title} />
          </div>
          <div className="product-details">
            <h2>{product.title}</h2>
            <p>{product.body}</p>
            <button className='show-prices-button' onClick={() => setShowPrices(!showPrices)}>
              Show Prices
            </button>
            {showPrices && (
              <div className="prices">
                <p>Regular price: {product.prices[0].price} $</p>
                <p>Member price: {product.prices[0].member_price} $</p>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Product;
